(2154, 'Oppenheimer', 'Christopher Nolan', 'Drama, History', 'United Kingdom, United States of America', 'en', 'The story of J. Robert Oppenheimer\'s role in the development of the atomic bomb during World War II.', 181, 'Cillian Murphy, Emily Blunt, Matt Damon, Robert Downey Jr., Florence Pugh', '2023-07-19', 'https://image.tmdb.org/t/p/w500/8Gxv8gSFCU0XGDykEGv7zR1n2ua.jpg', 'Golden Globe Award for Best Motion Picture – Drama, Academy Award for Best Picture, BAFTA Award for Best Film, Academy Award for Best Director, Academy Award for Best Actor, Academy Award for Best Supporting Actor', '', '', 8.065, 0, 0),
(2155, 'Operation Undead', 'Kongkiat Khomsiri', 'Horror, Action, Thriller', 'Singapore, Thailand', 'th', 'Inexperienced Thai soldiers battle a growing undead menace in this gruesome survival horror. A Japanese military experiment that turns men into monsters escapes containment, it’s up to these troops to save their nation from annihilation.', 110, 'Chanon Santinatornkul, Awat Ratanapintha, Guntapat Kasemsan Na Ayudhya, Thawatchanin Darayon, Thanadol Auepong', '2024-07-20', 'https://image.tmdb.org/t/p/w500/rs10lMBnJFnJX1Y7MYpyB8JiNAF.jpg', '', '', '', 3.8, 0, 0),
(2156, 'Night Call', 'Michiel Blanchart', 'Thriller, Action', 'Belgium, France', 'fr', 'Mady, a student, works as a locksmith by night. He helps Claire get into her apartment and soon realizes that she lied to him about her identity and robbed something that belonged to a dangerous man, Yannick. Mady gets embroiled in a manhunt and will have one night to prove his innocence.', 97, 'Jonathan Feltre, Natacha Krief, Jonas Bloquet, Romain Duris, Thomas Mustin', '2024-08-28', 'https://image.tmdb.org/t/p/w500/5NYSdTdM1A4WKDmxwoph7PSFunv.jpg', 'Pégase Award for best indie video game', 'Pégase Award for best indie video game, Pégase Award for best first video game, Pégase Award for best narrative design', '', 6.776, 0, 0),
(2157, 'The Beekeeper', 'David Ayer', 'Action, Crime, Thriller', 'United Kingdom, United States of America', 'en', 'One man\'s campaign for vengeance takes on national stakes after he is revealed to be a former operative of a powerful and clandestine organization known as Beekeepers.', 105, 'Jason Statham, Emmy Raver-Lampman, Josh Hutcherson, Bobby Naderi, Minnie Driver', '2024-01-08', 'https://image.tmdb.org/t/p/w500/A7EByudX0eOzlkQ2FIbogzyazm2.jpg', '', 'Swiss Film Award for Best Documentary Film', '', 7.3, 0, 0),
(2158, 'The Pursuit of Lust', 'Batdelger Byambasvren', 'Romance, Drama', 'Mongolia', 'mn', 'Sensual Mongolian actress E.Khandmaa stars in this romantic drama, which highlights a housewife’s longing for pleasure her passionless husband fails to provide. That is until her spouse’s business client eventually gives in to her desires.', 38, 'STEPINSTONE, E.KHANDMAA, E.BATKHULEG', '2025-01-23', 'https://image.tmdb.org/t/p/w500/affsHDxbgTpCHQbC5GVyZCbuukO.jpg', '', '', '', 5.833, 0, 0),
(2159, 'Conclave', 'Edward Berger', 'Drama, Thriller, Mystery', 'United Kingdom, United States of America', 'en', 'After the unexpected death of the Pope, Cardinal Lawrence is tasked with managing the covert and ancient ritual of electing a new one. Sequestered in the Vatican with the Catholic Church’s most powerful leaders until the process is complete, Lawrence finds himself at the center of a conspiracy that could lead to its downfall.', 120, 'Ralph Fiennes, Stanley Tucci, John Lithgow, Isabella Rossellini, Lucian Msamati', '2024-10-25', 'https://image.tmdb.org/t/p/w500/m5x8D0bZ3eKqIVWZ5y7TnZ2oTVg.jpg', '', '', '', 7.2, 0, 0);
