(3042, 'Mother of the Bride', 'Mark Waters', 'Romance, Comedy', 'Thailand, United States of America', 'en', 'Lana\'s daughter Emma returns from abroad and drops a bombshell: she\'s getting married. In Thailand. In a month! Things only get worse when Lana learns that the man who captured Emma\'s heart is the son of the man who broke hers years ago.', 90, 'Brooke Shields, Miranda Cosgrove, Benjamin Bratt, Sean Teale, Rachael Harris', '2024-05-08', 'https://image.tmdb.org/t/p/w500/vdTvwykMWvVgdaViBVRh8IFTku5.jpg', '', 'International Submission to the Academy Awards', '', 5.932, 0, 0),
(3043, 'The Taste of Things', 'Trần Anh Hùng', 'Romance, Drama', 'Belgium, France', 'fr', 'Set in 1889 France, Dodin Bouffant is a chef living with his personal cook and lover Eugénie. They share a long history of gastronomy and love but Eugénie refuses to marry Dodin, so the food lover decides to do something he has never done before: cook for her.', 135, 'Benoît Magimel, Juliette Binoche, Patrick d\'Assumçao, Emmanuel Salinger, Jan Hammenecker', '2023-11-08', 'https://image.tmdb.org/t/p/w500/2NjpbQUIJD7jkARB6EJbOxZ9JVC.jpg', '', '', '', 7.1, 0, 0),
(3044, 'BOCCHI THE ROCK! Recap Part 1', 'Keiichiro Saito', 'Animation, Comedy, Music', 'Japan', 'ja', 'Revisit events from Bocchi joining Kessoku Band to their first successful gig.', 90, 'Yoshino Aoyama, Sayumi Suzushiro, Saku Mizuno, Ikumi Hasegawa, Maaya Uchida', '2024-06-07', 'https://image.tmdb.org/t/p/w500/gv5swmEUyrrTBuhNdb8rTCmsimm.jpg', '', '', '', 6.6, 0, 0),
(3045, 'The Painter', 'Kimani Ray Smith', 'Action, Thriller', 'United States of America', 'en', 'An ex-CIA operative is thrown back into a dangerous world when a mysterious woman from his past resurfaces. Now exposed and targeted by a relentless killer and a rogue black ops program, he must rely on skills he thought he left behind in a high-stakes game of survival.', 100, 'Charlie Weber, Jon Voight, Madison Bailey, Max Montesi, Marie Avgeropoulos', '2024-01-05', 'https://image.tmdb.org/t/p/w500/UZ0ydgbXtnrq8xZCI5lHVXVcH9.jpg', '', '', '1982 Cannes Film Festival', 6.203, 0, 0),
(3046, 'His Three Daughters', 'Azazel Jacobs', 'Drama, Comedy', 'United States of America', 'en', 'Emotions run high when three estranged sisters reunite in a cramped New York City apartment to watch over their ailing father during his final days.', 104, 'Carrie Coon, Natasha Lyonne, Elizabeth Olsen, Jay O. Sanders, Jovan Adepo', '2024-09-05', 'https://image.tmdb.org/t/p/w500/yTIHBPLQyzhgz1c2spak3btJxsg.jpg', '', '', '', 7.038, 0, 0),
(3047, 'War of the Worlds: The Attack', 'Junaid Syed', 'Science Fiction, Mystery, Thriller', 'United Kingdom', 'en', 'Three young astronomers fight to survive a deadly Martian invasion.', 87, 'Sam Gittins, Leo Staar, Vincent Regan, Alhaji Fofana, Lara Lemon', '2023-04-21', 'https://image.tmdb.org/t/p/w500/wtar1NytLPRnd8hlwspeRRW5NVm.jpg', '', '', '', 5, 0, 0);
